mapper.update:
UPDATE tb_gd_sku t
SET
t.disabled = 1,
t.updateTime = #{updateTime},
t.updateUser = #{updateUser}
where t.id = #{id}

mapper.select:
select t.* from tb_gd_sku t
where t.disabled = 0
AND t.productId = #{productId}

select count(1) from tb_gd_sku t
where t.disabled = 0
AND t.productId = #{productId}
