mapper.select:
select

from tb_od_order t
where t.disabled = 0 and t.id = #{id}

select

from tb_od_order t
where t.disabled = 0 and t.number = #{number}

select , e.name expressName from tb_od_order t LEFT JOIN tb_ep_express e ON t.expressId = e.id AND e.disabled = 0 where t.disabled = 0
and t.status = #{status}
and t.userId = #{userId}
order by t.createTime desc

select
COUNT(1)
from tb_od_order t
where t.disabled = 0

    and t.status = #{status}


    and t.userId = #{userId}
