mapper.update:
update tb_od_order_sku t
t.name = #{count},
t.updateUser = #{updateUser},
t.updateTime = #{updateTime}
where t.id = #{id}

mapper.select:
select t.* from tb_od_order_sku t
where t.disabled = 0
AND t.orderId = #{orderId}

select count(1) from tb_od_order_sku t
where t.disabled = 0
AND t.productId = #{productId}
