mapper.select:
select count(*) from v_sys_user_role t where t.disabled = 0 and t.roleId = #{roleId}
and t.userName like CONCAT('%', #{txtUserName},'%')
and t.account like CONCAT('%', #{txtUserAccount},'%')

mapper.insert:
insert into tb_sys_user_role
(userId, roleId, createTime, createUser, updateTime, updateUser, disabled)
values(#{userId}, #{roleId}, now(), #{createUser}, now(), #{updateUser}, 0);
